"use strict";function parallax(){var e=window.pageYOffset;document.querySelector(".header__text").style.transform="translateY("+1*-e+"px)"}function navigationfix(){var e=document.querySelector("#intro").offsetHeight;window.pageYOffset>e?document.querySelector(".nav").classList.add("nav_fixed"):window.pageYOffset<e&&document.querySelector(".nav").classList.remove("nav_fixed")}function scroll(e,t){var o=document.body,n=e-o.scrollTop,r=n/t*10;setTimeout(function(){o.scrollTop=o.scrollTop+r,o.scrollTop!=e&&scroll(e,t-10)},10)}window.addEventListener("scroll",function(){parallax(),navigationfix()}),document.addEventListener("DOMContentLoaded",function(){document.querySelector(".link_nav").onclick=function(){var e=this.getAttribute("href");return scroll(document.querySelector(e).scrollTop,1500),!1}});